t/css/logo/index.html.ittf.contents:
$group
    $params svg_width:float, l:float|0, t:float|0
    
    $
        var ratio = svg_width / 660;
        
        
        var l = l * 1.0;
        var t = t * 1.0;
        var x1 = 0;
        var x2 = 270;
        var x3 = 360;
        var x4 = 510;
        var x5 = 670;
        var opt = {};
        opt.y0 = 10 * ratio;
        opt.y1 = 10 * ratio;
        opt.y1a = 90 * ratio;
        opt.y2 = 210 * ratio;
        opt.ybottom = 250 * ratio;
        opt.stroke = '#01579b';
        opt.fill = '#01579b';
        //opt.fill = 'none';
        opt.ratio = ratio;

    $if l > 0
        w( ${l + x1 * ratio}, ${t}, ${ratio}, &opt )
        i( ${l + x2 * ratio}, ${t}, ${ratio}, &opt )
        zeta( ${l + x3 * ratio}, ${t}, ${ratio}, &opt )
        zeta( ${l + x4 * ratio}, ${t}, ${ratio}, &opt )
        i( ${l + x5 * ratio}, ${t}, ${ratio}, &opt )	
    $else
        svg
            width ${700 * ratio}
            height ${260 * ratio}
            rect
                x 0
                y 0
                width 700
                height 280
                fill none
                stroke none
            w( ${l + x1 * ratio}, ${t}, ${ratio}, &opt )
            i( ${l + x2 * ratio}, ${t}, ${ratio}, &opt )
            zeta( ${l + x3 * ratio}, ${t}, ${ratio}, &opt )
            zeta( ${l + x4 * ratio}, ${t}, ${ratio}, &opt )
            i( ${l + x5 * ratio}, ${t}, ${ratio}, &opt )

t/css/logo/t/i.html.ittf.contents:
$group
	$params l:float, t:float, ratio:float, &opt

	polyline
		stroke ${opt.stroke}
		fill ${opt.fill}
		stroke-width 2
		points ${l} ${t+opt.ybottom} 
			\b ${l+10*opt.ratio} ${t+opt.y1+80*opt.ratio}
			\b ${l+40*opt.ratio} ${t+opt.y1+80*opt.ratio} 
			\b ${l+50*opt.ratio} ${t+opt.ybottom} 

    path
		stroke white
		fill ${opt.fill}
		stroke-width 2
		d M${l+30*ratio}, ${t+opt.y1+20*ratio} 
			\b C${l+60*ratio},${t+opt.y1+40*ratio} 
			\b ${l+0*ratio},${t+opt.y1+60*ratio} 
			\b ${l+30*ratio}, ${t+opt.y1+74*ratio}

t/css/logo/t/w.html.ittf.contents:
$group
	$params l:float, t:float, ratio:float, &opt

    polyline
        stroke ${opt.stroke}
        fill ${opt.fill}
        stroke-width 2
        points ${l + 10 * ratio} ${t + opt.ybottom}
            \b ${l + 45 * ratio} ${t + opt.y0} 
            \b ${l + 60 * ratio} ${t + opt.y0} 
            \b ${l + 75 * ratio} ${t + opt.y1a + (40*ratio)} 
            \b ${l + 120 * ratio} ${t + opt.y2} 
            \b ${l + 120 * ratio} ${t + opt.y1a + (40*ratio)} 
            \b ${l + 175 * ratio} ${t + opt.y2} 
            \b ${l + 175 * ratio} ${t + opt.y1a + (20*ratio)} 
            \b ${l + 230 * ratio} ${t + opt.y2} 
            \b ${l + 230 * ratio} ${t + opt.ybottom}

t/css/logo/t/zeta.html.ittf.contents:
$group
	$params l:float, t:float, ratio:float, &opt
	
	polyline
		stroke ${opt.stroke}
		fill ${opt.fill}
		stroke-width 2
		
        points ${l} ${t+opt.ybottom} 
			\b ${l} ${t+opt.ybottom - 20*opt.ratio}
			\b ${l+100*opt.ratio} ${t+opt.y1a+40*opt.ratio} 
            \b ${l+80*opt.ratio} ${t+opt.y1a+20*opt.ratio}
			\b ${l+80*opt.ratio} ${t+opt.y1a} 
			\b ${l+130*opt.ratio} ${t+opt.y1a} 
			\b ${l+70*opt.ratio} ${t+opt.ybottom - 20*opt.ratio}
			\b ${l+120*opt.ratio} ${t+opt.ybottom - 20*opt.ratio}
			\b ${l+120*opt.ratio} ${t+opt.ybottom}

