t/ecma_builtins.ittf.ittf.contents:
$group

	element Ecma builtin objects
		tag
		ast 
		category

		item
			title Ecma builtin functions
			ittf
				root
					node parseInt('43.5') = ${d}{ parseInt(43.5) }
					node parseFloat('43.5') = ${d}{ parseFloat(43.5) }
					node isFinite(Infinity) = ${d}{ isFinite(Infinity) }
					node isFinite(NaN) = ${d}{ isFinite(NaN) }
					node isFinite(null) = ${d}{ isFinite(null) }
					node isFinite(0) = ${d}{ isFinite(0) }
					node Number.isFinite(null) = ${d}{ Number.isFinite(null) }
					node Number.isFinite(0) = ${d}{ Number.isFinite(0) }
					node isNaN(NaN) = ${d}{ isNaN(NaN) }
					node isNaN(undefined) = ${d}{ isNaN(undefined) }
					node isNaN({}) = ${d}{ isNaN({})}
					node isNaN(true) = ${d}{ isNaN(true) }
					node isNaN(null) = ${d}{ isNaN(null) }
					node isNaN(37) = ${d}{ isNaN(37) }
					node isNaN('37') = ${d}{ isNaN('37') }
					node isNaN('37.37') = ${d}{ isNaN('37.37') }
					node isNaN('123ABC') = ${d}{ isNaN('123ABC') }
					node parseInt('123ABC') = ${d}{ parseInt('123ABC') }
					node Number('123ABC') = ${d}{ Number('123ABC') }
					node isNaN('') = ${d}{ isNaN('') }
					node Number('') = ${d}{ Number('') }
					node isNaN(' ') = ${d}{ isNaN(' ') }
					node Number(' ') = ${d}{ Number(' ') }
					node isNaN(new Date()) = ${d}{ isNaN(new Date()) }
					node isNaN(new Date().toString()) = ${d}{ isNaN(new Date().toString()) }
					node isNaN('blabla') = ${d}{ isNaN('blabla') } 
					node Number('blabla') = ${d}{ Number('blabla') } 
					node parseFloat(3.14) = ${d}{ parseFloat(3.14) }
					node parseFloat('3.14') = ${d}{ parseFloat('3.14') }
					node parseFloat('314e-2') = ${d}{ parseFloat('314e-2') }
					node parseFloat('0.0314E+2') = ${d}{ parseFloat('0.0314E+2') }
					node parseFloat('3.14more non-digit characters') = ${d}{parseFloat('3.14more non-digit characters') }
					node parseInt(' 0xF', 16) = ${d}{ parseInt(' 0xF', 16) }
					node parseInt(' F', 16) = ${d}{ parseInt(' F', 16) }
					node parseInt('17', 8) = ${d}{ parseInt('17', 8) }
					$$ not allowed in strict mode : parseInt(021, 8) = parseInt(021, 8) }
					node parseInt('015', 10) = ${d}{ parseInt('015', 10) }
					node parseInt(15.99, 10) = ${d}{ parseInt(15.99, 10) }
					node parseInt('15,123', 10) = ${d}{ parseInt('15,123', 10) }
					node parseInt('FXX123', 16) = ${d}{ parseInt('FXX123', 16) }
					node parseInt('1111', 2) = ${d}{ parseInt('1111', 2) }
					node parseInt('15 * 3', 10) = ${d}{ parseInt('15 * 3', 10) }
					node parseInt('15e2', 10) = ${d}{ parseInt('15e2', 10) }
					node parseInt('15px', 10) = ${d}{ parseInt('15px', 10) }
					node parseInt('12', 13) = ${d}{ parseInt('12', 13) }
					node set1 // Reserved Characters', set1 }
					node set2 // Unescaped Characters', set2 }
					node set3 // Number Sign', set3 }
					node set4 // Alphanumeric Characters + Space', set4 }
					node reserved chars : encodeURI(";,/?:@&=+$") = ${d}{ encodeURI(";,/?:@&=+$") }
					node unescaped chars : encodeURI("-_.!~*'()") = ${d}{ encodeURI("-_.!~*'()") }
					node number sign: encodeURI("#") = ${d}{ encodeURI("#") }
					node alphanumeric chars + space : encodeURI("ABC abc 123") = ${d}{ encodeURI("ABC abc 123") }

					node encodeURIComponent(";,/?:@&=+$") = ${d}{ encodeURIComponent(";,/?:@&=+$") }
					node encodeURIComponent("-_.!~*'()") = ${d}{ encodeURIComponent("-_.!~*'()") }
					node encodeURIComponent("#") = ${d}{ encodeURIComponent("#") }
					node encodeURIComponent("ABC abc 123") = ${d}{ encodeURIComponent("ABC abc 123") }

		item 
			title Ecma builtin String functions
			ittf
				root
					node ${d}{'a'.toUpperCase()}
					node ${d}{'A'.toLowerCase()}
					node ${d}{'a' + '  b  ' + 'c'}
					node ${d}{'a' + '  b  '.trim() + 'c'}
					node ${d}{'a,b,c,d'.split(',').join('-')}
		
		item 
			title Operations on String objects passed as parameters
			ittf
				root
					${d}params commandParam|alpha;beta;gamma
					${d}
						var commands = [];
						var actions = commandParam.split(';')
						for (var i=0; i<actions.length; i++) {
							var tokens = actions[i].trim().split(' ');
							var command = { name: tokens[0] };
							commands.push (command);
						}
					node Commands: ${d}{ _.inspect(commands) }

		item
			title Ecma builtin Date object
			ittf
				root
					node <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Date"></a>
					node today: new Date() = ${d}{ new Date() }
					node new Date('December 17, 1995 03:24:00') = ${d}{ new Date('December 17, 1995 03:24:00') }
					node new Date('1995-12-17T03:24:00') = ${d}{ new Date('1995-12-17T03:24:00') }
					node new Date(1995, 11, 17) = ${d}{ new Date(1995, 11, 17) }
					node new Date(1995, 11, 17, 3, 24, 0) = ${d}{ new Date(1995, 11, 17, 3, 24, 0) }

		item
			title Ecma builtin Number object
			ittf
				root
					node Number.MAX_VALUE  = ${d}{ Number.MAX_VALUE }
					node Number.MIN_VALUE = ${d}{ Number.MIN_VALUE }
					node Number.POSITIVE_INFINITY = ${d}{ Number.POSITIVE_INFINITY }
					node Number.NEGATIVE_INFINITY = ${d}{ Number.NEGATIVE_INFINITY }
					node Number.NaN = ${d}{ Number.NaN }
					node Number('123') = ${d}{ Number('123') }
					node Number('12.3') = ${d}{ Number('12.3') }
					node Number('') = ${d}{ Number('') }
					node Number('0x11') = ${d}{ Number('0x11') }
					node Number('0b11') = ${d}{ Number('0b11') }
					node Number('0o11') = ${d}{ Number('0o11') }
					node Number('foo') = ${d}{ Number('foo') }
					node Number('100a') = ${d}{ Number('100a') }

		item
			title Ecma builtin RegExp object
			ittf
				root
					node <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/RegExp"></a>
					node new RegExp('\\w+') = ${d}{ new RegExp('\\w+') }
					node 'John Smith'.replace(new RegExp('(\\w+)\\s(\\w+)'), '$2, $1') = 
						${contb} ${d}{ 'John Smith'.replace(new RegExp('(\\w+)\\s(\\w+)'), '$2, $1') } 
					${d}
						var str = 'John Smith';
						var reValue = '(\\w+)\\s(\\w+)';
						var re = new RegExp(reValue);
					node str.replace(re, '$2, $1') = ${d}{ str.replace(re, '$2, $1') } 

		item
			title Ecma builtin Array object
			ittf
				root
					node <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array"></a>
					node new Array(4) = ${d}{ new Array(4) }
					node new Array(4).join('0') = ${d}{ new Array(4).join('0') }

		item
			title Ecma builtin Object object
			ittf
				root
					node <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Object"></a>
					node new Object(true) = ${d}{ new Object(true).toString() }

t/functions.ittf.ittf.contents:
$group

	element jsWizzi functions
		tag
		ast 
		category

		item
			title Functions in scripts
			ittf
				root
					${d}
						function sayHello(name) {
							return 'Hii I am ' + name;
						}
						${d}${d}TODO var sh = sayHello;

					p If Varenne could have talked he would have said: ${d}{ sayHello('Varenne') }.
					${d}${d}TODO p If Ribot could have talked he would have said: ${d}{ sh('Ribot') }.

		item
			title Functions in markup
			ittf
				root
					${d}function sayHello
						${d}param name
						node Hii I am ${d}{ name }

					${d} var name = 'Ribot'
					${d}_ sayHello
						${d}@ name


		item
			title Functions in scripts + markup !!! powerful
			ittf
				root
					${d}
						function buildNode(parent, childCount, deep) {
							parent.childs = [];
							if (deep > 3) { return; }
							parent.childs = [];
							for (var i = 0; i < childCount; i++) {
								var child = { id: parent.id + '-' + i};
								buildNode (child, childCount, deep + 1);
								parent.childs.push (child);
							}
						}
						var tree = { id: 0 };
						buildNode (tree, 2, 0);

					node
					node Let us inspect the 'tree' variable we built.
					node
					node ${d}{ _.inspect(tree) }

					node
					node beautiful tree buildup without depth limits
					node
	
					${d}function nodeView
						${d}param parent
						div node${d}{parent.id}
							${d}foreach nodeChild in parent.childs
								${d}_ nodeView
									${d}@ nodeChild

					${d}_ nodeView
						${d}@ tree

t/jswizzi.ittf.ittf.contents:
$group

	element jsWizzi
		tag
		ast 
		category

		item
			title jsWizzi variable declaration and interpolation
			ittf
				root
					${d}
						var name = 'stefi';
						var options = {
							title: 'Chapter 1'
						};
					node title is ${d}{options.title}
					node name is ${d}{name}

		item 
			title More variable declarations
			ittf
				html
					${d}
						var name = 'Ribot';
						var runDowns = 16, runWon = 16;
						var placements = [1, 1, 1, 1, 1];
						var pedigree = {
							mother: 'Romanella',
							father: 'Tenerani',
							paternalGrandfather: 'Bellini',
							maternalGrandfather: 'El greco'
						};

					p ${d}{name} was undefeated. Won ${d}{runWon} out of ${d}{runDowns} races.
					p His father was ${d}{pedigree.father} and his mother ${d}{pedigree.mother}.
					p His placements are boring: ${d}{placements}, ...

		item 
			title Inpute parameters with default values
			ittf
				root
					${d}params name|stefi, age:integer|61, inlove:boolean|true
					node ${d}{name}, age ${d}{age}, ${d}{inlove ? 'is' : 'is not'} in love.

		item
			title Unary expressions
			ittf
				any
					any +2 =  ${d}{ +2 }
					any -2 = ${d}{ -2 }
					any !(2 == 2) = ${d}{ !(2 == 2) }
					${d}
						var a = 0;
					any a = = ${d}{ a }
					any ++a = ${d}{ ++a }
					any ++a = ${d}{ ++a }
					any --a = ${d}{ --a }
					any typeof 3 = ${d}{ typeof 3 }
					any typeof '3' = ${d}{ typeof '3' }
					any typeof xyz = ${d}{ typeof xyz }

		item
			title Expressions
			ittf
				any
					any 2 + 2 = ${d}{ 2 + 2 }
					any 5 - 2 = ${d}{ 5 - 2 }
					any 3 * 3 = ${d}{ 3 * 3 }
					any 6 / 2 = ${d}{ 6 / 2 }
					any 5 % 2 = ${d}{ 5 % 2 }
					any 5 == 2 = ${d}{ 5 == 2 }
					any 5 != 2 = ${d}{ 5 != 2 }
					any 5 > 2 = ${d}{ 5 > 2 }
					any 5 >= 5 = ${d}{ 5 >= 5 }
					any 5 < 2 = ${d}{ 5 < 2 }
					any 5 <= 5 = ${d}{ 5 <= 5 }
					any "a" in { a: 1} = ${d}{ "a" in { a: 1} }
					any typeof 5 === 'string' = ${d}{ typeof 5 === 'string' }
					any String(5) = ${d}{ String(5) }
					any Number('5.5') = ${d}{ Number('5.5') }
					any Date(2013,6,12) = ${d}{ Date(2013,6,12) }
					any 5 << 5 = ${d}{ 5 << 5 }
					any 5 >> 5 = ${d}{ 5 >> 5 }
					any 5 >>> 5 = ${d}{ 5 >>> 5 }
					any 5 | 5 = ${d}{ 5 | 5 }
					any 5 & 5 = ${d}{ 5 & 5 }
					any 5 ^ 5 = ${d}{ 5 ^ 5 }


		item 
			title Conditional operator
			ittf
				root
					node (3 == 3 ? 1 : 0) = ${d}{ 3 == 3 ? 1 : 0 }
					node (true ? 5 : 2) = ${d}{ true ? 5 : 2 }


		item 
			title Assignments
			ittf
				root
					${d} var a = 0;
					node var a = 0; a += 2; -> ${d}{ a += 2 }
					${d} var a = 0;
					node var a = 0; a -= 2; -> ${d}{ a -= 2 }
					${d} var a = 3;
					node var a = 3; a *= 2; -> ${d}{ a *= 2 }
					${d} var a = 3;
					node var a = 3; a /= 2; -> ${d}{ a /= 2 }
					${d} var a = 3;
					node var a = 3; a %= 2; -> ${d}{ a %= 2 }
					${d} var a = 3;
					node var a = 3; a <<= 2; -> ${d}{ a <<= 2 }
					${d} var a = 3;
					node var a = 3; a >>= 2; -> ${d}{ a >>= 2 }
					${d} var a = 3;
					node var a = 3; a >>>= 2; -> ${d}{ a >>>= 2 }
					${d} var a = 3;
					node var a = 3; a &= 2; -> ${d}{ a &= 2 }
					${d} var a = 3;
					node var a = 3; a |= 2; -> ${d}{ a |= 2 }
					${d} var a = 3;
					node var a = 3; a ^= 2; -> ${d}{ a ^= 2 }

t/jswizzi_builtins.ittf.ittf.contents:
$group

	element jsWizzi builtin objects
		tag
		ast 
		category

		item
			title 'inflect' builtin functions
			ittf
				root
					node 'item'.pluralize = ${d}{ 'item'.pluralize }
					node 'friend'.pluralize = ${d}{ 'friend'.pluralize }
					node 'lady'.pluralize = ${d}{ 'lady'.pluralize }
					node 'way'.pluralize.toUpperCase() = ${d}{ 'way'.pluralize.toUpperCase() }
					node 'people'.singularize = ${d}{ 'people'.singularize }
					node 'octopi'.singularize = ${d}{ 'octopi'.singularize }
					node 'Hats'.singularize.toUpperCase() = ${d}{ 'Hats'.singularize.toUpperCase() }
					node 'message_properties'.camelize = ${d}{ 'message_properties'.camelize }
					node 'message_properties'.camelize = ${d}{ 'message_properties'.camelize }
					node 'message_properties'.dasherize = ${d}{ 'message_properties'.dasherize }
					node 'Message Properties'.dasherize = ${d}{ 'Message Properties'.dasherize }
					node 'message_properties'.titleize = ${d}{ 'message_properties'.titleize }
					node 'message properties to keep'.titleize = ${d}{ 'message properties to keep'.titleize }
					node 'Message.Bus.Properties'.demodulize = ${d}{ 'Message.Bus.Properties'.demodulize }
					node 'MessageBusProperty'.tableize = ${d}{ 'MessageBusProperty'.tableize }
					node 'message_bus_properties'.classify = ${d}{ 'message_bus_properties'.classify }
					node 'MessageBusProperty'.foreign_key = ${d}{ 'MessageBusProperty'.foreign_key }
					node '1'.ordinalize = ${d}{ '1'.ordinalize }
					node '2'.ordinalize = ${d}{ '2'.ordinalize }
					node '3'.ordinalize = ${d}{ '3'.ordinalize }
					node '4'.ordinalize = ${d}{ '4'.ordinalize }

		item
			title 'jsWizzi _' builtin functions
			ittf
				root
					node inflect : 'message-properties'.camelize = ${d}{ 'message-properties'.camelize }
					node jsWizzi _ : _.dashToCamelCase('message-properties') = ${d}{ _.dashToCamelCase('message-properties') }
					node inflect : 'message_properties'.camelize = ${d}{ 'message_properties'.camelize }
					node jsWizzi _ : _.camelize('message_properties') = ${d}{ _.camelize('message_properties') }
					${d} var text = 'alfa\\t " " \\n'
					node "_.escape( text )" = "${d}{ _.escape( text ) }"
					node _.isString( '3' ) = ${d}{ _.isString( '3' ) }
					node _.isString( 3 ) = ${d}{ _.isString( 3 ) }
					node _.isEmpty( '' ) = ${d}{ _.isEmpty( '' ) }
					node _.isEmpty( 'a ' ) = ${d}{ _.isEmpty( 'a ' ) }
					node _.isNotEmpty( 'a ' ) = ${d}{ _.isNotEmpty( 'a ' ) }
					node _.isNotEmpty( '' ) = ${d}{ _.isNotEmpty( '' ) }
					node _.isObject( {} ) = ${d}{ _.isObject( {} ) }
					node _.isObject( 3 ) = ${d}{ _.isObject( 3 ) }
					node _.isArray( [] ) = ${d}{ _.isArray( [] ) }
					node _.isArray( {} ) = ${d}{ _.isArray( {} ) }
					node _.isNumber( 3 ) = ${d}{ _.isNumber( 3 ) }
					node _.isNumber( '' ) = ${d}{ _.isNumber( '' ) }
					node _.isDate( new Date() ) = ${d}{ _.isDate( new Date() ) }
					node _.isDate( [] ) = ${d}{ _.isDate( [] ) }
					${d}
						var options = {
							title: 'Chapter 1'
						};
					node _.replace( '1353739', '3', '0' ) = ${d}{ _.replace( '1353739', '3', '0' ) }
					node _.inspect( options ) = ${d}{ _.inspect( options ) }

t/magics.ittf.ittf.contents:
$group

	element Ittf magic tricks
		tag
		ast 
		category

		item 
			title Embedding statements in interpolated expressions
			ittf
				root
					${d}
						var commands = [ { name: 'alpha' }, { name: 'beta' }, { name: 'gamma' } ];
					node We perform an interpolation of a multistatement jsWizzi expression.
					node We intersperse command names with dashes.
					${d}foreach command in commands
						node  ${d}{ command.name.toUpperCase()[0] + ' - ' + command.name }
						node  This is quite crazy. But it works! -> ${d}{
							${contb} var len = command.name.length, output = '';
							${contb} for (var i=0; i<len; i++) {
							${contb}   output += command.name[i];
							${contb}   if (i < len -1) { output += ' - '};
							${contb} }
							${contb} return output;
							${contb} }
					node  That is why mTree stays for "magical tree".
					node  See in wizzi-mtree/loader/ittfinterpolate.js how this works.

t/statements.ittf.ittf.contents:
$group

	element jsWizzi statements
		tag
		ast 
		category


		item
			title Conditions in scripts
			ittf
				root
					${d}
						var name = 'Derby', winner = null;
						if (name == 'Ascot') {
							winner = 'Ribot';
						} else if (name == 'Arc de triomphe') {
							winner = 'Varenne';
						} else if (name == 'Derby') {
							winner = 'Ribot';
						} else {
							winner = 'Varenne';
						}
					node If the race was ${d}{name} the winner was ${d}{winner}.

		item
			title Conditions in markup
			ittf
				root
					${d}
						var name = 'Derby';
					${d}if name == 'Ascot'
						node If the race was ${d}{name} the winner was Ribot
					${d}elif name == 'Arc de triomphe'
						node If the race was ${d}{name} the winner was Varenne
					${d}elif name == 'Derby'
						node If the race was ${d}{name} the winner was Ribot
					${d}else
						node If the race was ${d}{name} the winner was Varenne

		item
			title For loop in scripts
			ittf
				root
					${d}
						var sletters = 'a,b,c,d,e,f,g,h';
						var aletters = sletters.split(',');
						var letter, label='';
						for (var i=0; i<aletters.length; i++) {
							letter = aletters[i];
							label += i % 2 == 0 ? letter : letter.toUpperCase();
						}
					node The resulting label has the even letters upper cased: ${d}{label}.

		item
			title For loop in markup
			ittf
				root
					${d}
						var sletters = 'a,b,c,d,e,f,g,h';
						var aletters = sletters.split(',');
					node The resulting tree has the even node values upper cased:
						${d}foreach letter in aletters
							node ${d}{letter_index} ${d}{letter_index % 2 == 0 ? letter : letter.toUpperCase() }

		item
			title While loop in scripts
			ittf
				root
					${d}
						var x = 1, odds = '1';
						while ( x < 10 ) {
							x += 2;
							odds += ', ' + x 
						}
					node First odd numbers: ${d}{odds}.

		item
			title While loop in markup
			ittf
				root
					${d} var x = 1, odds = '1'
					${d}while x < 10
						${d} x += 2
						node ${d}{ odds += ', ' + x }

