t/advanced.ittf.ittf.contents:
element advanced
	tag
	ast 
	category
	
	item
		title Intersection Types
		expected
			+ function extend<T, U>(first: T, second: U): T & U {
				+ let result = {} as T & U;
				+ for (let id in first) {
					+ (result as any)[id] = (first as any)[id];
				+ }
				+ for (let id in second) {
					+ if (!result.hasOwnProperty(id)) {
						+ (result as any)[id] = (second as any)[id];
					+ }
				+ }
				+ return result;
			+ }
			+ class Person {
				+ constructor(public name: string) {}
			+ }
			+ interface Loggable {
				+ log(): void;
			+ }
			+ class ConsoleLogger implements Loggable {
				+ log() {
					+ // ...
				+ }
			+ }
			+ var jim = extend(new Person("Jim"), new ConsoleLogger());
			+ var n = jim.name;
			+ jim.log();

		ittf
			+
				function extend
					:< T
					:< U
					param first
						:ref T
					param second
						:ref U
					:return
						:intersect 
							:ref T
							:ref U
					let result
						:as 
							:intersect 
								:ref T
								:ref U
							{ {}
					for let id in first
						set =
							@expr 
								( 
									@id result
										:as 
											:any 
								.[ id
							@expr 
								( 
									@id first
										:as 
											:any 
								.[ id
					for let id in second
						if !result.hasOwnProperty(id)
							set =
								@expr 
									( 
										@id result
											:as 
												:any 
									.[ id
								@expr 
									( 
										@id second
											:as 
												:any 
									.[ id
					return result
				class Person
					ctor 
						param name
							:string 
				:interface Loggable
					:m log
						:void 
				class ConsoleLogger
					:extends Loggable
					m log
				var jim = extend(new Person("Jim"), new ConsoleLogger())
				var n = jim.name
				_ jim.log

	item
		title Union Types - fail at run time
		expected
			+ function padLeft(value: string, padding: any) {
				+ if (typeof padding === "number") {
					+ return Array(padding + 1).join(" ") + value;
				+ }
				+ if (typeof padding === "string") {
					+ return padding + value;
				+ }
				+ throw new Error(`Expected string or number, got '${padding}'.`);
			+ }
			+ padLeft("Hello world", 4); // returns "    Hello world"
			+ let indentedString = padLeft("Hello world", true); // passes at compile time, fails at runtime.
		
		ittf
			+
				function padLeft
					param value
						:string 
					param padding
						:any 
					if typeof padding === "number"
						return Array(padding + 1).join(" ") + value
					if typeof padding === "string"
						return padding + value
					throw 
						new Error
							`lit 
								+ Expected string or number, got '
								@ padding
								+ '.
				_ padLeft("Hello world", 4)
				let indentedString = padLeft("Hello world", true)
	item
		title Union Types - succeds at run time
		expected
			+ function padLeft(value: string, padding: string | number) {
				+ // ...
			+ }
			+ let indentedString = padLeft("Hello world", true); // errors during compilation

		ittf
			+
				function padLeft
					param value
						:string 
					param padding
						:union 
							:string 
							:number 
				let indentedString = padLeft("Hello world", true)
				
	item
		title Union Types - common members
		expected
			+ // If we have a value that has a union type, we can only access members that are common to all types in the union.
			+ interface Bird {
				+ fly();
				+ layEggs();
			+ }
			+ interface Fish {
				+ swim();
				+ layEggs();
			+ }
			+ function getSmallPet(): Fish | Bird {
				+ // ...
			+ }
		ittf
			+
				:interface Bird
					:m fly
					:m layEggs
				:interface Fish
					:m swim
					:m layEggs
				function getSmallPet
					:return
						:union 
							:ref Fish
							:ref Bird
				let pet = getSmallPet()
				_ pet.layEggs
				_ pet.swim

	item
		title Type Guards and Differentiating Types
		expected
			+ let pet = getSmallPet();
			+ if ((pet as Fish).swim) {
				+ (pet as Fish).swim();
			+ } else {
				+ (pet as Bird).fly();
			+ }
		ittf
			+
				let pet = getSmallPet()
				if 
					test
						@expr 
							( 
								@id pet
									:as 
										:ref Fish
							. swim
					_ 
						( 
							@id pet
								:as 
									:ref Fish
						._ swim
				else
					_ 
						( 
							@id pet
								:as 
									:ref Bird
						._ fly
    
	item
		title User - Defined Type Guards
		expected
			+ function isFish(pet: Fish | Bird): pet is Fish {
				+ return (pet as Fish).swim !== undefined;
			+ }
			+ function isNumber(x: any): x is number {
				+ return typeof x === "number";
			+ }
			+ function isString(x: any): x is string {
				+ return typeof x === "string";
			+ }
			+ function padLeft(value: string, padding: string | number) {
				+ if (isNumber(padding)) {
					+ return Array(padding + 1).join(" ") + value;
				+ }
				+ if (isString(padding)) {
					+ return padding + value;
				+ }
				+ throw new Error(`Expected string or number, got '${padding}'.`);
			+ }

		ittf
			+
				function isFish
					param pet
						:union 
							:ref Fish
							:ref Bird
					:return
						:predicate pet
							:ref Fish
					return 
						!== 
							@expr 
								( 
									@id pet
										:as 
											:ref Fish
								. swim
							+ undefined
				function isNumber
					param x
						:any 
					:return
						:predicate x
							:number 
					return typeof x === "number"
				function isString
					param x
						:any 
					:return
						:predicate x
							:string 
					return typeof x === "string"
				function padLeft
					param value
						:string 
					param padding
						:union 
							:string 
							:number 
					if isNumber(padding)
						return Array(padding + 1).join(" ") + value
					if isString(padding)
						return padding + value
					throw 
						new Error
							`lit 
								+ Expected string or number, got '
								@ padding
								+ '.

	item
		title Instanceof type guards
		expected
			+ interface Padder {
				+ getPaddingString(): string
			+ }
			+ class SpaceRepeatingPadder implements Padder {
				+ constructor(private numSpaces: number) {}
				+ getPaddingString() {
					+ return Array(this.numSpaces + 1).join(" ");
				+ }
			+ }
			+ class StringPadder implements Padder {
				+ constructor(private value: string) { }
				+ getPaddingString() {
					+ return this.value;
				+ }
			+ }
			+ function getRandomPadder() {
				+ return Math.random() < 0.5 ?
					+ new SpaceRepeatingPadder(4) :
					+ new StringPadder("  ");
			+ }
			+ // Type is 'SpaceRepeatingPadder | StringPadder'
			+ let padder: Padder = getRandomPadder();
			+ if (padder instanceof SpaceRepeatingPadder) {
				+ var x = padder; // type narrowed to 'SpaceRepeatingPadder'
			+ }
			+ if (padder instanceof StringPadder) {
				+ var x = padder; // type narrowed to 'StringPadder'
			+ }

		ittf
			+
				:interface Padder
					:m getPaddingString
						:string 
				class SpaceRepeatingPadder
					:extends Padder
					ctor 
						param numSpaces
							:private 
							:number 
					m getPaddingString
						return Array(this.numSpaces + 1).join(" ")
				class StringPadder
					:extends Padder
					ctor 
						param value
							:private 
							:string 
					m getPaddingString
						return this.value
				function getRandomPadder
					return 
						iif Math.random() < 0.5
							then new SpaceRepeatingPadder(4)
							else new StringPadder("  ")
				# Type is 'SpaceRepeatingPadder | StringPadder'
				let padder
					:ref Padder
					_ getRandomPadder
				if padder instanceof SpaceRepeatingPadder
					var x = padder
					# type narrowed to 'SpaceRepeatingPadder'
				if padder instanceof StringPadder
					var x = padder
					# type narrowed to 'StringPadder'

	item
		title Nullable types
		expected
			+ let s = "foo";
			+ s = null; // error, 'null' is not assignable to 'string'
			+ let sn: string | null = "bar";
			+ sn = null; // ok
			+ sn = undefined; // error, 'undefined' is not assignable to 'string | null'
			+ // Note that TypeScript treats null and undefined differently in order to match JavaScript semantics. 
			+ // string | null is a different type than string | undefined and string | undefined | null.
			+ /*
				+ Optional parameters and properties
				+ With --strictNullChecks, an optional parameter automatically adds | undefined:
			+ */
			+ function f(x: number, y?: number) {
				+ return x + (y || 0);
			+ }
			+ f(1, 2);
			+ f(1);
			+ f(1, undefined);
			+ f(1, null); // error, 'null' is not assignable to 'number | undefined'
			+ // The same is true for optional properties:
			+ class C {
				+ a: number;
				+ b?: number;
			+ }
			+ let c = new C();
			+ c.a = 12;
			+ c.a = undefined; // error, 'undefined' is not assignable to 'number'
			+ c.b = 13;
			+ c.b = undefined; // ok
			+ c.b = null; // error, 'null' is not assignable to 'number | undefined'
		
		ittf
			+
				let s = "foo"
				set s = null
				let sn
					:union 
						:string 
						:null 
					:= "bar"
				set sn = null
				set sn = undefined
				function f
					param x
						:number 
					param 
						:number 
						:optional 
					return x + y || 0
				_ f(1, 2)
				_ f(1)
				_ f(1, undefined)
				_ f(1, null)
				class C
					p a
						:number 
					p b
						:number 
				let c = new C()
				set c.a = 12
				set c.a = undefined
				set c.b = 13
				set c.b = undefined
				set c.b = null

	item
		title Type guards and type assertions
		expected
			+ function f(sn: string | null): string {
				+ if (sn == null) {
					+ return "default";
				+ } else {
					+ return sn;
				+ }
			+ }
			+ // The null elimination is pretty obvious here, but you can use terser operators too:
			+ function f(sn: string | null): string {
				+ return sn || "default";
			+ }
			+ // In cases where the compiler cant eliminate null or undefined, 
			+ // you can use the type assertion operator to manually remove them. 
			+ // The syntax is postfix !: identifier! removes null and undefined from the type of identifier:
			+ function broken(name: string | null): string {
				+ function postfix(epithet: string) {
					+ return name.charAt(0) + '.  the ' + epithet; // error, 'name' is possibly null
				+ }
				+ name = name || "Bob";
				+ return postfix("great");
			+ }
			+ function fixed(name: string | null): string {
				+ function postfix(epithet: string) {
					+ return name!.charAt(0) + '.  the ' + epithet; // ok
				+ }
				+ name = name || "Bob";
				+ return postfix("great");
			+ }
		ittf
			+
				function f
					param sn
						:union 
							:string 
							:null 
					:return
						:string 
					if sn == null
						return "default"
					else
						return sn
				function f
					param sn
						:union 
							:string 
							:null 
					:return
						:string 
					return sn || "default"
				function broken
					param name
						:union 
							:string 
							:null 
					:return
						:string 
					function postfix
						param epithet
							:string 
						return name.charAt(0) + '.  the ' + epithet
					set name = name || "Bob"
					return postfix("great")
				function fixed
					param name
						:union 
							:string 
							:null 
					:return
						:string 
					function postfix
						param epithet
							:string 
						return 
							op+ 
								op+ 
									_ 
										._ charAt
											@ 0
									+ '.  the '
								+ epithet
					set name = name || "Bob"
					return postfix("great")

	item
		title Type Aliases
		expected
			+ type Name = string;
			+ type NameResolver = () => string;
			+ type NameOrResolver = Name | NameResolver;
			+ function getName(n: NameOrResolver): Name {
				+ if (typeof n === "string") {
					+ return n;
				+ } else {
					+ return n();
				+ }
			+ }
			+ // Aliasing doesnt actually create a new type - it creates a new name to refer to that type. 
			+ // Aliasing a primitive is not terribly useful, though it can be used as a form of documentation.
			+ // Just like interfaces, type aliases can also be generic - we can just add type parameters and use them on the right side of the alias declaration:
			+ type Container<T> = { value: T };
			+ // We can also have a type alias refer to itself in a property:
			+ type Tree<T> = {
				+ value: T;
				+ left: Tree<T>;
				+ right: Tree<T>;
			+ }
			+ // Together with intersection types, we can make some pretty mind-bending types:
			+ type LinkedList<T> = T & { next: LinkedList<T> };
			+ interface Person {
				+ name: string;
			+ }
			+ var people: LinkedList<Person>;
			+ var s = people.name;
			+ var s = people.next.name;
			+ var s = people.next.next.name;
			+ var s = people.next.next.next.name;
			+ // However, its not possible for a type alias to appear anywhere else on the right side of the declaration:
			+ type Yikes = Array<Yikes>; // error

		ittf
			+
				:type Name
					:string 
				:type NameResolver
					:=> 
						:string 
				:type NameOrResolver
					:union 
						:ref Name
						:ref NameResolver
				function getName
					param n
						:ref NameOrResolver
					:return
						:ref Name
					if typeof n === "string"
						return n
					else
						return n()
				:type Container
					:< T
					:{ 
						:p value
							:ref T
				:type Tree
					:< T
					:{ 
						:p value
							:ref T
						:p left
							:ref Tree
								:ref T
						:p right
							:ref Tree
								:ref T
				:type LinkedList
					:< T
					:intersect 
						:ref T
						:{ 
							:p next
								:ref LinkedList
									:ref T
				:interface Person
					:p name
						:string 
				var people
					:ref LinkedList
						:ref Person
				var s = people.name
				var s = people.next.name
				var s = people.next.next.name
				var s = people.next.next.next.name
				:type Yikes
					:ref Array
						:ref Yikes

	item
		title Interfaces vs.Type Aliases
		expected
			+
		ittf
			+
				:type Alias
					:{ 
						:p num
							:number 
				:interface Interface
					:p num
						:number 
				:function aliased
					param arg
						:ref Alias
					:return
						:ref Alias
				:function interfaced
					param arg
						:ref Interface
					:return
						:ref Interface
	
	item
		title String Literal Types
		expected
			+ type Easing = "ease-in" | "ease-out" | "ease-in-out";
			+ class UIElement {
				+ animate(dx: number, dy: number, easing: Easing) {
					+ if (easing === "ease-in") {
					+ } else if (easing === "ease-out") {
					+ } else if (easing === "ease-in-out") {
					+ } else {
						+ // error! should not pass null or undefined.
					+ }
				+ }
			+ }
			+ let button = new UIElement();
			+ button.animate(0, 0, "ease-in");
			+ button.animate(0, 0, "uneasy"); // error: "uneasy" is not allowed here
			+ // You can pass any of the three allowed strings, but any other string will give the error
			+ // Argument of type '"uneasy"' is not assignable to parameter of type '"ease-in" | "ease-out" | "ease-in-out"'
			+ // String literal types can be used in the same way to distinguish overloads:
			+ function createElement(tagName: "img"): HTMLImageElement;
			+ function createElement(tagName: "input"): HTMLInputElement;
			+ // ... more overloads ...
			+ function createElement(tagName: string): Element {
				+ // ... code goes here ...
			+ }

		ittf
			+
				:type Easing
					:union 
						:literal "ease-in"
						:literal "ease-out"
						:literal "ease-in-out"
				class UIElement
					m animate
						param dx
							:number 
						param dy
							:number 
						param easing
							:ref Easing
						if easing === "ease-in"
						else
							if easing === "ease-out"
							else
								if easing === "ease-in-out"
								else
				let button = new UIElement()
				_ button.animate(0, 0, "ease-in")
				_ button.animate(0, 0, "uneasy")
				:function createElement
					param tagName
						:literal "img"
					:return
						:ref HTMLImageElement
				:function createElement
					param tagName
						:literal "input"
					:return
						:ref HTMLInputElement
				function createElement
					param tagName
						:string 
					:return
						:ref Element

	item
		title Numeric Literal Types
		expected
			+ function rollDie(): 1 | 2 | 3 | 4 | 5 | 6 {
			+ }
			+ // These are seldom written explicitly, they can be useful when narrowing can catch bugs:
			+ function foo(x: number) {
				+ if (x !== 1 || x !== 2) {
					+ // Operator '!==' cannot be applied to types '1' and '2'.
				+ }
			+ }
			+ // In other words, x must be 1 when it gets compared to 2, meaning that the above check is making an invalid comparison.

		ittf
			+
				function rollDie
					:return
						:union 
							:literal 1
							:literal 2
							:literal 3
							:literal 4
							:literal 5
							:literal 6
				function foo
					param x
						:number 
					if x !== 1 || x !== 2

	item
